def _download_zip(zipname='m_theory_library.zip'):
  import subprocess
  from google.colab import files
  try:
    os.unlink(zipname)
  except OSError:
    pass
  subprocess.run(['zip', '-9', '-r', zipname, 'm_theory', '-x', '*.pyc'])
  files.download(zipname)


